/*
 * Simplistic interface to Public API
 *
 */
const Request = require('superagent');
const baseURL = 'https://api.smartrecruiters.com';

function tokenHeader(token) {
    return `Bearer ${token}`;
}

function aboutMe(token, cb) {
    Request.get(baseURL + '/user-api/v201804/users/me')
        .set('Authorization', tokenHeader(token))
        .end(cb);
}

function jobs(token, cb) {
    Request.get(baseURL + '/jobs')
        .set('Authorization', tokenHeader(token))
        .end(cb);
}

module.exports = {
    aboutMe: aboutMe,
    jobs: jobs
};